import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom'; // Import useLocation
import axios from 'axios';

function ListUser() {
  const [users, setUsers] = useState([]);
  const [accessLevels, setAccessLevels] = useState([]);
  const location = useLocation(); // Use useLocation hook to get query parameters

  useEffect(() => {
    getUsers();
    getAccessLevels();
  }, []);

  function getUsers() {
    axios.get('http://localhost/rosales_freshten/api/employees/').then(function(response) {
      setUsers(response.data);
    });
  }

  function getAccessLevels() {
    axios.get('http://localhost/rosales_freshten/api/access_levels/').then(function(response) {
      setAccessLevels(response.data);
    });
  }

  const deleteUser = (id) => {
    axios.delete(`http://localhost/rosales_freshten/api/user/${id}/delete`).then(function(response){
      getUsers();
    }).catch(error => {
      console.error('Error deleting user: ', error);
    });
  }

  const getAccessLevelDescription = (accessLevelId) => {
    const accessLevel = accessLevels.find(level => level.access_level_id === accessLevelId);
    return accessLevel ? accessLevel.description : 'Unknown';
  }

  return (
    <div>
      <h1>List Users</h1>
      <div>
        <p>Email: {new URLSearchParams(location.search).get('email')}</p> {/* Display email from query parameters */}
        <p>Access Level Description: {new URLSearchParams(location.search).get('access_level')}</p> {/* Display access level description from query parameters */}
      </div>
      <table>
        <thead>
          <tr>
            <th>Employee ID</th>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Age</th>
            <th>Birth Date</th>
            <th>Email</th>
            <th>Password</th>
            <th>Date Created</th>
            <th>Job Title</th>
            <th>Access Level Description</th>
            <th>Date Modified</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user, key) =>
            <tr key={key}>
              <td>{user.employee_id}</td>
              <td>{user.firstname}</td>
              <td>{user.lastname}</td>
              <td>{user.age}</td>
              <td>{user.birth_date}</td>
              <td>{user.email}</td>
              <td>{user.password}</td>
              <td>{user.date_created}</td>
              <td>{user.job_title}</td>
              <td>{getAccessLevelDescription(user.access_level_id)}</td>
              <td>{user.date_modified}</td>
              <td>
                <button onClick={() => deleteUser(user.employee_id)}>Delete</button>
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}

export default ListUser;
