import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [accessLevels, setAccessLevels] = useState([]); // State to store access levels
  const [selectedAccessLevel, setSelectedAccessLevel] = useState(''); // State to store selected access level
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch access levels when component mounts
    getAccessLevels();
  }, []);

  const getAccessLevels = () => {
    // Fetch access levels from the server
    axios.get('http://localhost/rosales_freshten/api/access_levels/')
      .then(response => {
        setAccessLevels(response.data); // Set access levels in state
      })
      .catch(error => {
        console.error('Error fetching access levels: ', error);
      });
  };

  const handleLogin = (event) => {
    event.preventDefault();
    // Find the selected access level description based on its ID
    const selectedAccessLevelDescription = accessLevels.find(level => level.access_level_id === parseInt(selectedAccessLevel, 10))?.description;

    axios.post('http://localhost/rosales_freshten/api/login.php', { email, password, access_level_id: selectedAccessLevel })
      .then(response => {
        if (response.data.status === 1) {
          navigate(`/list?email=${email}&access_level=${selectedAccessLevelDescription}`); // Redirect with query parameters
        } else {
          alert(response.data.message);
        }
      })
      .catch(error => {
        console.error('Error logging in: ', error);
      });
  };

  return (
    <div className="flex justify-center items-center h-screen bg-gray-100">
      <div className="bg-white p-8 rounded shadow-md w-96">
        <h2 className="text-3xl font-semibold mb-4">Login</h2>
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="email">
            Email Address
          </label>
          <input
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            id="email"
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="mb-6">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="password">
            Password
          </label>
          <input
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 mb-3 leading-tight focus:outline-none focus:shadow-outline"
            id="password"
            type="password"
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <div className="flex items-center justify-between">
          <button
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            type="button"
            onClick={handleLogin}
          >
            Sign In
          </button>
          <a
            className="inline-block align-baseline font-bold text-sm text-blue-500 hover:text-blue-800"
            href="#"
          >
            Forgot Password?
          </a>
        </div>
      </div>
    </div>
  );
};

export default Login;
