import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [accessLevelId, setAccessLevelId] = useState('');
  const navigate = useNavigate();

  const handleLogin = (event) => {
    event.preventDefault();
    axios.post('http://localhost/rosales_freshten/api/login.php', { email, password, access_level_id: accessLevelId })
      .then(response => {
        if (response.data.status === 1) {
          navigate(`/list?email=${email}&access_level=${accessLevelId}`); // Redirect with query parameters
        } else {
          alert(response.data.message);
        }
      })
      .catch(error => {
        console.error('Error logging in: ', error);
      });
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <label>
          Email:
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
        </label>
        <br />
        <label>
          Password:
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </label>
        <br />
        <label>
          Access Level ID:
          <input type="number" value={accessLevelId} onChange={(e) => setAccessLevelId(e.target.value)} />
        </label>
        <br />
        <button type="submit">Login</button>
      </form>
    </div>
  );
}

export default Login;
