import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function CreateAccessLevel() {
    const navigate = useNavigate();
    const [description, setDescription] = useState('');

    const handleChange = (event) => {
        setDescription(event.target.value);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        const newAccessLevel = { description };

        axios.post('http://localhost/rosales_freshten/api/access_levels/save', newAccessLevel)
            .then(response => {
                console.log(response.data);
                navigate('/'); // Redirect to home page or any other desired page
            })
            .catch(error => {
                console.error('Error adding access level: ', error);
            });
    };

    return (
        <div>
            <h1>Add Access Level</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Description:
                    <input type="text" name="description" value={description} onChange={handleChange} required />
                </label>
                <br />
                <button type="submit">Add Access Level</button>
            </form>
        </div>
    );
}
